import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Observable } from 'rxjs';
import { Publicador } from './publicador';
import { PublicadorService } from '../publicador.service';

@Component({
  selector: 'app-publicador',
  templateUrl: './publicador.component.html',
  styleUrls: ['./publicador.component.css']
})
export class PublicadorComponent implements OnInit {
  colunas = ['id', 'nome', 'sexo', 'flBatismo', 'perfil', 'publicadorTipo', 'observacao', 'acoes'];
  publicadores: Publicador[] = [];
  formulario: FormGroup;
  publicadorSelecionado: Publicador = new Publicador("","","",);

  constructor(
    private publicadorService: PublicadorService,
    private fb: FormBuilder
  ) { }

  ngOnInit(): void {
    this.formulario = this.fb.group({
      nome: ['', Validators.required],
      sexo: ['', Validators.required],
      flBatismo: [false],
      perfil: ['', Validators.required],
      publicadorTipo: ['', Validators.required],
      observacao: ['']
    });
    this.listarPublicadores();
  }

  listarPublicadores() {
    this.publicadorService.list().subscribe((publicadores: Publicador[]) => {
      this.publicadores = publicadores;
    });
  }

  submit() {
    const publicador = this.formulario.value as Publicador;
    if (!this.publicadorSelecionado) {
      this.publicadorService.save(publicador).subscribe(() => {
        this.formulario.reset();
        this.listarPublicadores();
      });
    } else {
      publicador.id = this.publicadorSelecionado.id;
      this.publicadorService.atualizar(publicador).subscribe(() => {
        this.formulario.reset();
        this.publicadorSelecionado = undefined;
        this.listarPublicadores();
      });
    }
  }

  editar(publicador: Publicador) {
    this.publicadorSelecionado = publicador;
    this.formulario.patchValue({
      nome: publicador.nome,
      sexo: publicador.sexo,
      flBatismo: publicador.flBatismo,
      perfil: publicador.perfil,
      publicadorTipo: publicador.publicadorTipo,
      observacao: publicador.observacao
    });
  }

  excluir(publicador: Publicador) {
    this.publicadorService.deletar(publicador).subscribe(() => {
      this.listarPublicadores();
    });
  }

  cancelar() {
    this.formulario.reset();
    this.publicadorSelecionado = undefined;
  }

  isNovoPublicador(): boolean {
    return !this.publicadorSelecionado;
  }
}

