                        <th mat-header-cell *matHeaderCellDef>Batismo</th>
                        <td mat-cell *matCellDef="let publicador">{{publicador.flBatismo}}</td>
                    </ng-container>
        
                    <ng-container matColumnDef="perfil">
                        <th mat-header-cell *matHeaderCellDef>Perfil</th>
                        <td mat-cell *matCellDef="let publicador">{{publicador.perfil}}</td>
                    </ng-container>
                    
                    <ng-container matColumnDef="publicadorTipo">
                        <th mat-header-cell *matHeaderCellDef>Publicador Tipo</th>
                        <td mat-cell *matCellDef="let publicador">{{publicador.publicadorTipo}}</td>
                    </ng-container>
                    
                    <ng-container matColumnDef="observacao">
                        <th mat-header-cell *matHeaderCellDef>Observacao</th>
                        <td mat-cell *matCellDef="let publicador">{{publicador.observacao}}</td>
                    </ng-container>

                    <ng-container matColumnDef="acoes">
                        <th mat-header-cell *matHeaderCellDef>Ações</th>
                        <td mat-cell *matCellDef="let publicador">
                            <button mat-icon-button (click)="editar(publicador)">
                              <mat-icon>edit</mat-icon>
                            </button>
                        
                            <button mat-icon-button (click)="excluir(publicador)">
                            <mat-icon>delete</mat-icon>
                          </button>
                        </td>
                    </ng-container>
                    
                    <tr mat-header-row *matHeaderRowDef="colunas"></tr>
                    <tr mat-row *matRowDef="let row; columns: colunas;"></tr>                               
                </table>
        </mat-card>
    </mat-tab>
    <mat-tab>
        <ng-template mat-tab-label>
            Novo
        </ng-template>

        <mat-card class="full-width">
            <mat-card-title>Novo Publicador</mat-card-title>
            <form [formGroup]="formulario" (ngSubmit)="submit()" class="example-form">
                <mat-form-field class="example-full-width">
                    <mat-label>Nome: </mat-label>
                    <input formControlName="nome" matInput>
                </mat-form-field>

