                        <mat-label>Observação</mat-label>
                        <textarea matInput formControlName="observacao"></textarea>
                    </mat-form-field>
                </p>

                <div class="button-row">
                    <button mat-flat-button color="primary">Cadastrar</button>
                </div>
            </form>
        </mat-card>
    </mat-tab>
