import { Component, OnInit, ViewChild } from '@angular/core';
import { MatTabGroup } from '@angular/material/tabs';
import { PublicadorService } from '../publicador.service';
import { Publicador } from './publicador';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-publicador',
  templateUrl: './publicador.component.html',
  styleUrls: ['./publicador.component.css']
})
export class PublicadorComponent implements OnInit {
  @ViewChild(MatTabGroup) tabGroup!: MatTabGroup;

  formulario: FormGroup;
  publicadores: Publicador[] = [];
  colunas = ['id','nome','sexo','flBatismo','perfil','publicadorTipo','observacao', 'acoes'];
  publicadorSelecionado: Publicador = new Publicador('', '', false, '', '', '');

  constructor(
    private service : PublicadorService,
    private fb : FormBuilder
  ){
    this.formulario = this.fb.group({});
    this.publicadorSelecionado = new Publicador('', '', false, '', '', '');
  }

  ngOnInit(): void {
    this.montarFormulario();
    this.listarPublicadores();
  }

  montarFormulario(){
    this.formulario = this.fb.group({
      nome : ['', Validators.required],
        sexo : ['', Validators.required],
        flBatismo : ['', Validators.required],
        perfil : ['', Validators.required],
        publicadorTipo : ['', Validators.required],
        observacao : ['', Validators.required],
    })
  }

  listarPublicadores(){
    this.service.list().subscribe(response => {
      this.publicadores = response;
    })
  }

  submit() {
    const publicador = this.formulario.value as Publicador;
    if (!this.publicadorSelecionado) {
      this.service.save(publicador).subscribe(() => {
        this.formulario.reset();
        this.listarPublicadores();
      });
    } else {
      publicador.id = this.publicadorSelecionado.id;
      this.service.atualizar(publicador).subscribe(() => {
        this.formulario.reset();
        this.publicadorSelecionado;
        this.listarPublicadores();
      });
    }
  }
  
  excluir(publicador: Publicador){
    this.service.deletar(publicador).subscribe(() => {
      this.publicadores = this.publicadores.filter(p => p !== publicador);
    });
  }
  
  editar(publicador: Publicador): void {
    this.publicadorSelecionado = publicador;
    this.formulario.patchValue(publicador);
    this.tabGroup.selectedIndex = 1;
  }

  atualizar(){
    const formValues = this.formulario.value
    const publicador : Publicador = new Publicador(formValues.nome, formValues.sexo, formValues.flBatismo, formValues.perfil, formValues.publicadorTipo, formValues.observacao)
    this.service.atualizar(publicador).subscribe(resposta => {
      const index = this.publicadores.findIndex(p => p.id === publicador.id);
      this.publicadores[index] = resposta;
      this.publicadorSelecionado;
    });
  }
}
    <mat-toolbar>
        <button mat-icon-button class="example-icon" aria-label="Example icon-button with menu icon">
        <mat-icon>menu</mat-icon>
        </button>
        <span>Publicadores</span>
    </mat-toolbar>

    <mat-tab-group animationDuration="0ms" class="grupo-todos-e-novo">
    <mat-tab class="label">
        <ng-template mat-tab-label class="label-todos">
            Todos
        </ng-template>
        
        <mat-card>
            <div class="mat-elevation-z8">
                <table mat-table [dataSource]="dataSource">
              
                  <!-- Position Column -->
                  <ng-container matColumnDef="position">
                    <th mat-header-cell *matHeaderCellDef> No. </th>
                    <td mat-cell *matCellDef="let element"> {{element.position}} </td>
                  </ng-container>
              
                  <!-- Name Column -->
                  <ng-container matColumnDef="name">
                    <th mat-header-cell *matHeaderCellDef> Name </th>
                    <td mat-cell *matCellDef="let element"> {{element.name}} </td>
                  </ng-container>
              
                  <!-- Weight Column -->
                  <ng-container matColumnDef="weight">
                    <th mat-header-cell *matHeaderCellDef> Weight </th>
                    <td mat-cell *matCellDef="let element"> {{element.weight}} </td>
                  </ng-container>
              
                  <!-- Symbol Column -->
                  <ng-container matColumnDef="symbol">
                    <th mat-header-cell *matHeaderCellDef> Symbol </th>
                    <td mat-cell *matCellDef="let element"> {{element.symbol}} </td>
                  </ng-container>
              
                  <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
                  <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
                </table>
              
                <mat-paginator [pageSizeOptions]="[5, 10, 20]"
                               showFirstLastButtons
                               aria-label="Select page of periodic elements">
                </mat-paginator>
              </div>
              




                <table mat-table [dataSource]="publicadores" class="example-full-width">
                    <ng-container matColumnDef="id">
                        <th mat-header-cell *matHeaderCellDef>ID</th>
                        <td mat-cell *matCellDef="let publicador">{{publicador.id}}</td>
                    </ng-container>
        
                    <ng-container matColumnDef="nome">
                        <th mat-header-cell *matHeaderCellDef>Nome</th>
                        <td mat-cell *matCellDef="let publicador">{{publicador.nome}}</td>
                    </ng-container>
        
                    <ng-container matColumnDef="sexo">
                        <th mat-header-cell *matHeaderCellDef>Sexo</th>
                        <td mat-cell *matCellDef="let publicador">{{publicador.sexo}}</td>
                    </ng-container>
        
                    <ng-container matColumnDef="flBatismo">
                        <th mat-header-cell *matHeaderCellDef>Batismo</th>
                        <td mat-cell *matCellDef="let publicador">{{publicador.flBatismo}}</td>
                    </ng-container>
        
                    <ng-container matColumnDef="perfil">
                        <th mat-header-cell *matHeaderCellDef>Perfil</th>
                        <td mat-cell *matCellDef="let publicador">{{publicador.perfil}}</td>
                    </ng-container>
                    
                    <ng-container matColumnDef="publicadorTipo">
                        <th mat-header-cell *matHeaderCellDef>Publicador Tipo</th>
                        <td mat-cell *matCellDef="let publicador">{{publicador.publicadorTipo}}</td>
                    </ng-container>
                    
                    <ng-container matColumnDef="observacao">
                        <th mat-header-cell *matHeaderCellDef>Observacao</th>
                        <td mat-cell *matCellDef="let publicador">{{publicador.observacao}}</td>
                    </ng-container>

                    <ng-container matColumnDef="acoes">
                        <th mat-header-cell *matHeaderCellDef>Ações</th>
                        <td mat-cell *matCellDef="let publicador">
                            <button mat-icon-button (click)="editar(publicador)">
                              <mat-icon>edit</mat-icon>
                            </button>
                        
                            <button mat-icon-button (click)="excluir(publicador)">
                            <mat-icon>delete</mat-icon>
                          </button>
                        </td>
                    </ng-container>
                    
                    <tr mat-header-row *matHeaderRowDef="colunas"></tr>
                    <tr mat-row *matRowDef="let row; columns: colunas;"></tr>                               
                </table>
        </mat-card>
    </mat-tab>

    <mat-tab class="novo">
        <ng-template mat-tab-label>
            Novo
        </ng-template>

        <mat-card class="full-width">
            <mat-card-title>Novo Publicador</mat-card-title>
            <form [formGroup]="formulario" (ngSubmit)="submit()" class="example-form">
                <mat-form-field class="example-full-width">
                    <mat-label>Nome: </mat-label>
                    <input formControlName="nome" matInput>
                </mat-form-field>

                <p>
                <label id="example-radio-group-label">Sexo: </label>
                <mat-radio-group formControlName="sexo" aria-label="Sexo:">
                    <mat-radio-button value="M">Masculino</mat-radio-button>
                    <mat-radio-button value="F">Feminino</mat-radio-button>
                </mat-radio-group>
                </p>
                
                <p>
                    <label id="example-radio-group-label">Batizado: </label>
                    <mat-radio-group formControlName="flBatismo" aria-label="Batizado:">
                        <mat-radio-button value="true">Sim</mat-radio-button>
                        <mat-radio-button value="false">Não</mat-radio-button>
                    </mat-radio-group>
                </p>

                <p>
                    <label id="example-radio-group-label">Perfil: </label>
                    <mat-radio-group formControlName="perfil" aria-label="Perfil:">
                        <mat-radio-button value="SM">Servo Ministerial</mat-radio-button>
                        <mat-radio-button value="AN">Ancião</mat-radio-button>
                        <mat-radio-button value="NP">Não Possui</mat-radio-button>
                    </mat-radio-group>
                </p>
                <p>
                    <label id="example-radio-group-label">Tipo: </label>
                    <mat-radio-group formControlName="publicadorTipo" aria-label="Tipo:">
                        <mat-radio-button value="PB">Publicador</mat-radio-button>
                        <mat-radio-button value="PA">Pioneiro Auxiliar</mat-radio-button>
                        <mat-radio-button value="PI">Pioneiro Auxiliar Indeterminado</mat-radio-button>
                        <mat-radio-button value="PR">Pioneiro Regular</mat-radio-button>
                    </mat-radio-group>
                </p>

                <p>
                    <mat-form-field class="example-full-width">
                        <mat-label>Observação</mat-label>
                        <textarea matInput formControlName="observacao"></textarea>
                    </mat-form-field>
                </p>

                <div class="button-row">
                    <button mat-flat-button color="primary">Cadastrar</button>
                </div>
            </form>
        </mat-card>
    </mat-tab>
    </mat-tab-group>
